CREATE TABLE xi_filelib_file (id INT AUTO_INCREMENT NOT NULL, folder_id INT NOT NULL, resource_id INT NOT NULL, fileprofile VARCHAR(255) NOT NULL, filename VARCHAR(255) NOT NULL, filelink VARCHAR(255) DEFAULT NULL, date_created DATETIME NOT NULL, status INT NOT NULL, uuid VARCHAR(36) NOT NULL, UNIQUE INDEX UNIQ_E860652454840E92 (filelink), UNIQUE INDEX UNIQ_E8606524D17F50A6 (uuid), INDEX IDX_E8606524162CB942 (folder_id), INDEX IDX_E860652489329D25 (resource_id), UNIQUE INDEX folderid_filename_unique (folder_id, filename), PRIMARY KEY(id)) ENGINE = InnoDB;
CREATE TABLE xi_filelib_folder (id INT AUTO_INCREMENT NOT NULL, parent_id INT DEFAULT NULL, foldername VARCHAR(255) NOT NULL, folderurl VARCHAR(5000) NOT NULL, uuid VARCHAR(36) NOT NULL, UNIQUE INDEX UNIQ_A5EA9E8BD17F50A6 (uuid), INDEX IDX_A5EA9E8B727ACA70 (parent_id), PRIMARY KEY(id)) ENGINE = InnoDB;
CREATE TABLE xi_filelib_resource (id INT AUTO_INCREMENT NOT NULL, hash VARCHAR(255) NOT NULL, mimetype VARCHAR(255) NOT NULL, filesize INT DEFAULT NULL, date_created DATETIME NOT NULL, versions LONGTEXT NOT NULL COMMENT '(DC2Type:array)', PRIMARY KEY(id)) ENGINE = InnoDB;
ALTER TABLE xi_filelib_file ADD CONSTRAINT FK_E8606524162CB942 FOREIGN KEY (folder_id) REFERENCES xi_filelib_folder (id);
ALTER TABLE xi_filelib_file ADD CONSTRAINT FK_E860652489329D25 FOREIGN KEY (resource_id) REFERENCES xi_filelib_resource (id);
ALTER TABLE xi_filelib_folder ADD CONSTRAINT FK_A5EA9E8B727ACA70 FOREIGN KEY (parent_id) REFERENCES xi_filelib_folder (id);
