CREATE TABLE xi_filelib_file (id INT NOT NULL, folder_id INT NOT NULL, mimetype VARCHAR(255) NOT NULL, fileprofile VARCHAR(255) NOT NULL, filesize INT DEFAULT NULL, filename VARCHAR(255) NOT NULL, filelink VARCHAR(255) DEFAULT NULL, date_uploaded TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, status INT NOT NULL, PRIMARY KEY(id));
CREATE UNIQUE INDEX UNIQ_E860652454840E92 ON xi_filelib_file (filelink);
CREATE INDEX IDX_E8606524162CB942 ON xi_filelib_file (folder_id);
CREATE UNIQUE INDEX folderid_filename_unique ON xi_filelib_file (folder_id, filename);
CREATE TABLE xi_filelib_folder (id INT NOT NULL, parent_id INT DEFAULT NULL, foldername VARCHAR(255) NOT NULL, folderurl VARCHAR(5000) NOT NULL, PRIMARY KEY(id));
CREATE INDEX IDX_A5EA9E8B727ACA70 ON xi_filelib_folder (parent_id);
CREATE SEQUENCE xi_filelib_file_id_seq INCREMENT BY 1 MINVALUE 1 START 1;
CREATE SEQUENCE xi_filelib_folder_id_seq INCREMENT BY 1 MINVALUE 1 START 1;
ALTER TABLE xi_filelib_file ADD CONSTRAINT FK_E8606524162CB942 FOREIGN KEY (folder_id) REFERENCES xi_filelib_folder(id) NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE xi_filelib_folder ADD CONSTRAINT FK_A5EA9E8B727ACA70 FOREIGN KEY (parent_id) REFERENCES xi_filelib_folder(id) NOT DEFERRABLE INITIALLY IMMEDIATE;